import { all, fork, call, put, take } from "redux-saga/effects";

function* watchLogIn() {
    yield take("LOG_IN");
}

function* watchLogOut() {
    yield take("LOG_OUT");
}

function* addPost() {
    yield take("ADD_POST");
}
export default function* rootSaga() {
    yield all([fork(watchLogIn), fork(watchLogOut), fork(addPost)]);
}
