import { all, fork, call, put, take } from "redux-saga/effects";

function* logIn() {
    yield call(logInAPI);
}

function* watchLogIn() {
    yield take("LOG_IN", logIn); //LOG_IN이라는 액션이 실행되면, logIn함수를 실행시킨다.
}

function* watchLogOut() {
    yield take("LOG_OUT");
}

function* addPost() {
    yield take("ADD_POST");
}
export default function* rootSaga() {
    yield all([fork(watchLogIn), fork(watchLogOut), fork(addPost)]); //fork는 괄호안에 있는 함수를 실행한다는 뜻
    //call과 fork는 차이가 있다.
    //all은 동시에 실행 시켜준다.
}
