import { all, fork, call, put, take, takeEvery } from "redux-saga/effects"; //saga의 이펙트 //all은 fork, call을 한번에 실행해준다. //put을 디스패치라고 생각하면 마음이 편한다.
//call과 Fork의 차이는 동기, 비동기 차이이다. call은 응답이 올때까지 대기.(일종의 브레이크)
import axios from "axios";
function logInAPI() {
    return axios.post("api/login");
}

function* logIn(action) {
    try {
        const result = yield call(logInAPI, action.data);
        yield put({
            //요청성공
            type: "LOG_IN_SUCCESS",
            data: result.data,
        });
    } catch (err) {
        yield put({
            //요청실패
            type: "LOG_IN_FAILURE",
            data: err.response.data,
        });
    }
}
function logOutAPI() {
    return axios.post("api/logout");
}

function* logOut(action) {
    try {
        const result = yield call(logOutAPI, action.data);
        yield put({
            //요청성공
            type: "LOG_OUT_SUCCESS",
            data: result.data,
        });
    } catch (err) {
        yield put({
            //요청실패
            type: "LOG_OUT_FAILURE",
            data: err.response.data,
        });
    }
}

function addPostAPI(data) {
    return axios.post("/api/post", data);
}

function* addPost(action) {
    try {
        const result = yield call(addPostAPI, action.data);
        yield put({
            //요청성공
            type: "ADD_POST_SUCCESS",
            data: result.data,
        });
    } catch (err) {
        yield put({
            //요청실패
            type: "ADD_POST_FAILURE",
            data: err.response.data,
        });
    }
}

function* watchLogIn() {
    yield takeEvery("LOG_IN_REQUEST", logIn); //while대신에 takeEvery
}
function* watchLogOut() {
    yield takeEvery("LOG_OUT_REQUEST", logOut);
}
function* watchAddPost() {
    yield takeEvery("ADD_POST_REQUEST");
}
export default function* rootSaga() {
    yield all([fork(watchLogIn), fork(watchLogOut), fork(watchAddPost)]);
}
