import meenoi1 from "../images/meenoi1";
import meenoi2 from "../images/meenoi2";
import meenoi3 from "../images/meenoi3";

export const initialValue = {
    mainPosts: [
        {
            id: 1,
            User: {
                id: 1,
                nickname: "이현철",
            },
            content: "첫 번째 게시글 #해시태그 #익스프레스",
            Images: [
                {
                    src: { meenoi1 },
                },
                {
                    src: { meenoi2 },
                },
                {
                    src: { meenoi3 },
                },
            ],
            Comments: [
                {
                    User: {
                        nickname: "nero",
                    },
                    content: "우와 개정판이 나왔군요~",
                },
                {
                    User: {
                        nickname: "hero",
                    },
                    content: "얼른 사고싶어요~",
                },
            ],
        },
    ],
    //이미지업로드 할떄 이미지경로들이 여기 들어간다.
    imagePaths: [],
    //게시글 추가가 완료되었을때 TRue
    postAdded: false,
};
//가짜 데이토

const ADD_POST = "ADD_POST";
export const addPost = {
    type: ADD_POST,
};

const dummyPost = {
    id: 2,
    content: "?.",
    User: {
        id: 1,
        nickname: "이현철",
    },
    Images: [],
    Comments: [],
};

const reducer = (state = initialValue, action) => {
    switch (action.type) {
        case ADD_POST:
            return {
                ...state,
                mainPosts: [dummyPost, ...state.mainPosts],
                postAdded: true,
            };
        default:
            return state;
    }
};

export default reducer;
