import { all, fork, call, put, take } from "redux-saga/effects";

function logInAPI() {
    //실제로 서버에 요청을 보내는 부분
    return axios.post("/api/login");
}

function* logIn() {
    try {
        yield put({ type: "LOG_IN_REQUEST" });
        const result = yield call(logInAPI); //로그인 요청에 대해 결과값으로 받을 수 있다.
        yield put({ type: "LOG_IN_SUCCESS", data: result.data });
    } catch (err) {
        put({ type: "LOG_IN_FAILURE", data: err.response.data });
    }
}

function* watchLogIn() {
    yield take("LOG_IN_REQUEST", logIn); //LOG_IN이라는 액션이 실행되면, logIn함수를 실행시킨다.
}

function* watchLogOut() {
    yield take("LOG_OUT");
}

function* addPost() {
    yield take("ADD_POST");
}
export default function* rootSaga() {
    yield all([fork(watchLogIn), fork(watchLogOut), fork(addPost)]); //fork는 괄호안에 있는 함수를 실행한다는 뜻
    //call과 fork는 차이가 있다.
    //all은 동시에 실행 시켜준다.
}
