import React, { useCallback, useState } from "react";
import PropTypes from "prop-types";
const PostImages = ({ images }) => {
    const [showImgesZoom, setShowImagesZoom] = useState(false);
    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, []);
    if (images.length === 1) {
        return (
            <>
                <img
                    role="presentaion"
                    src={images[0].src}
                    alt={images[0].src}
                    onClick={onZoom}
                />
            </>
        );
    }
    if (images.length === 2) {
        return (
            <>
                <img
                    role="presentaion"
                    src={images[0].src}
                    alt={images[0].src}
                    onClick={onZoom}
                    width="50%"
                />
                <img
                    role="presentaion"
                    src={images[1].src}
                    alt={images[1].src}
                    onClick={onZoom}
                    width="50%"
                />
            </>
        );
    }
    return (
        <>
            <div>
                <img
                    role="presentaion"
                    width="50%"
                    stc={images[0].src}
                    alt={images[0].src}
                    onClick={onZoom}
                />
            </div>
        </>
    );
};

PostImages.PropTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
};
export default PostImages;
